import {useSelector, useDispatch} from "react-redux"
import {sampleAction} from "../actions/builderActions"
import Fade from "react-reveal"

const Cart = () => {
    const combo = useSelector(state => state.builderReducer.combo)
    const dispatch = useDispatch()
    // useEffect(() => {          // example of syntax if needed
    //     return () => {
    //         cleanup
    //     }
    // }, [])
    console.log(combo)
    return (
    <>
    <Fade top>
        <div className="container">
            <div className="row mx-0 pb-3 justify-content-center">
                <img style={{width: "625px"}} src="img/kart-cart.png" />
            </div>
        </div>
    </Fade>
    <Fade bottom cascade>
        <div>
            {}
            <img src={`img/items/${combo[0].driver.img}`} />
