            <img src={`img/items/${combo[1].tire.img}`} />
            <img src={`img/items/${combo[1].glider.img}`} /><br />
            <img src={`img/items/${combo[2].driver.img}`} />
            <img src={`img/items/${combo[2].kart.img}`} />
            <img src={`img/items/${combo[2].tire.img}`} />
            <img src={`img/items/${combo[2].glider.img}`} /><br />
        </div>
    </Fade>
    </>
    )
}

export default Cart
