import {UPDATE_DRIVER} from "./types"
import {UPDATE_KART} from "./types"
import {UPDATE_TIRE} from "./types"
import {UPDATE_GLIDER} from "./types"

export const updateDriver = (driver) => {
    return {
        type: UPDATE_DRIVER,
        driver: driver
    }
}

export const updateKart = (kart) => {
    return {
        type: UPDATE_KART,
        kart: kart
    }
}
