import {UPDATE_DRIVER} from "./types"
import {UPDATE_KART} from "./types"
import {UPDATE_TIRE} from "./types"
import {UPDATE_GLIDER} from "./types"

export const updateDriver = (driverID) => {
    return {
        type: UPDATE_DRIVER,
        driverID: driverID
    }
}

export const updateKart = (kartID) => {
    return {
        type: UPDATE_KART,
        kartID: kart
    }
}

export const updateTire = (tire) => {
    return {
        type: UPDATE_TIRE,
        tire: tire
    }
}

export const updateGlider = (glider) => {
    return {
        type: UPDATE_GLIDER,
        glider: glider
    }
}
