    return (
    <>
        <Chart type="bar"
                data={{
                    labels: ["Weight", "Acceleration", "On-Road Traction", "Off-Road Traction", 5, 6, 7, 8, 9, 10, 11, 12, 13],
                    datasets: [
                        {
                            label: "Driver",
