import {useSelector, useDispatch} from "react-redux"
import {sampleAction} from "../actions/builderActions"
import Fade from "react-reveal"

const Cart = () => {
    const combos = useSelector(state => state.builderReducer.comboArr)
    const dispatch = useDispatch()
    // useEffect(() => {          // example of syntax if needed
    //     return () => {
    //         cleanup
    //     }
    // }, [])
    console.log(combos)
    return (
    <>
    <Fade top>
        <div className="container">
            <div className="row mx-0 pb-3 justify-content-center">
                <img style={{width: "625px"}} src="img/kart-cart.png" />
            </div>
        </div>
    </Fade>
    <Fade bottom cascade>
        <div>{combos.map(combo => {
            return (
                <>
                <img src={`img/items/${combo.driver.img}`} />
            <img src={`img/items/${combo.kart.img}`} />
            <img src={`img/items/${combo.tire.img}`} />
            <img src={`img/items/${combo.glider.img}`} /><br />
            </>
            )
        })}

            <img src={`img/items/${combos[1].driver.img}`} />
            <img src={`img/items/${combos[1].kart.img}`} />
            <img src={`img/items/${combos[1].tire.img}`} />
            <img src={`img/items/${combos[1].glider.img}`} /><br />
            <img src={`img/items/${combos[2].driver.img}`} />
            <img src={`img/items/${combos[2].kart.img}`} />
            <img src={`img/items/${combos[2].tire.img}`} />
            <img src={`img/items/${combos[2].glider.img}`} /><br />
        </div>
    </Fade>
    </>
    )
}

export default Cart
