import {UPDATE_DRIVER} from "./types"

export const updateDriver = (driver) => {
    return {
        type: UPDATE_DRIVER,
        driver: driver
    }
}

export const updateKart = (driver) => {
    return {
        type: UPDATE_DRIVER,
        driver: driver
    }
}
