import {UPDATE_DRIVER} from "./types"
import {UPDATE_KART} from "./types"
import {UPDATE_TIRE} from "./types"
import {UPDATE_GLIDER} from "./types"
import {LOAD_INIT} from "./types"
// import {LOAD_MKDATA} from "./types"
import MKData from "../assets/MK8-data.js"

// export const loadMKData = () => {
//     return {
//         type: LOAD_MKDATA,
//         MKData: MKData
//     }
// }

export const loadInit = () => {
    return {
        type: LOAD_INIT,
        initState: {
            driver: MKData[41],
            kart: {
                id: 1,
                name: "Standard Kart",
                type: "kart",
                img: "StandardKartBodyMK8.png",
                wg: 2,
                ac: 4,
                on: 3,
                of: 3,
                mt: 4,
                sl: 3,
                sw: 3,
                sa: 3,
                sg: 3,
                tl: 3,
                tw: 2,
                ta: 3,
                tg: 3
            },
            tire: {
                id: 84,
                name: "Standard",
                type: "tire",
                img: "StandardTiresMK8.png",
                wg: 2,
                ac: 4,
                on: 2,
                of: 5,
                mt: 3,
                sl: 2,
                sw: 3,
                sa: 2,
                sg: 3,
                tl: 3,
                tw: 3,
                ta: 3,
                tg: 3
            },
            glider: {
                id: 105,
                name: "Super Glider",
                type: "glider",
                img: "SuperGliderMK8.png",
                wg: 1,
                ac: 1,
                on: 1,
                of: 1,
                mt: 1,
                sl: 1,
                sw: 1,
                sa: 0,
                sg: 2,
                tl: 1,
                tw: 0,
                ta: 1,
                tg: 1
            },
            combo: [],
            MKData: MKData
        }
    }
}

export const updateDriver = (driverID) => {
    return {
        type: UPDATE_DRIVER,
        driverID: driverID
    }
}

export const updateKart = (kartID) => {
    return {
        type: UPDATE_KART,
        kartID: kartID
    }
}

export const updateTire = (tireID) => {
    return {
        type: UPDATE_TIRE,
        tireID: tireID
    }
}

export const updateGlider = (gliderID) => {
    return {
        type: UPDATE_GLIDER,
        gliderID: gliderID
    }
}
