import React, {useEffect, useState} from "react"
import {useSelector, useDispatch} from "react-redux"
import {loadInit, loadMKData, updateDriver, updateKart, updateTire, updateGlider} from "../actions/builderActions"
import "chart.js/auto"
import {Chart} from "react-chartjs-2"
import MKData from "../assets/MK8-data.js"
import Fade from "react-reveal/Fade"

const Builder = () => {
    const [formDriver, setFormDriver] = useState({})
    const [formKart, setFormKart] = useState({})
    const [formTire, setFormTire] = useState({})
    const [formGlider, setFormGlider] = useState({})
    const driverState = useSelector(state => state.builderReducer.driver)
    const kartState = useSelector(state => state.builderReducer.kart)
    const tireState = useSelector(state => state.builderReducer.tire)
    const gliderState = useSelector(state => state.builderReducer.glider)
    const dispatch = useDispatch()
    useEffect(() => {          // example of syntax if needed
        dispatch(loadInit())
        // dispatch(loadMKData())
    }, [])
    const karts = MKData.filter(item => item.type === "kart")
    const drivers = MKData.filter(item => item.type === "driver")
    const tires = MKData.filter(item => item.type === "tire")
    const gliders = MKData.filter(item => item.type === "glider")

    return (
    <>
        <div>
            <Fade top>
                <div className="row mx-0">
                    <div className="col d-flex justify-content-center">
                    <form>
                        <div>
                            <img src={`img/items/${driverState.img}`} />
                        </div>
                        <div>
                        <select name="drivers" id="drivers" value={formDriver} onChange={(e) => setFormDriver(e.target.value)}>
                            {drivers.map(driver => {
                                return <option value={driver.id}>{driver.name}</option>
                            })}
                        </select><br />
                        <button type="button" value="submit" onClick={() => dispatch(updateDriver(formDriver))}>Update Driver</button>
                        </div>
                    </form>
                    </div>
                    <div className="col d-flex justify-content-center">
                    <form>
                        <div>
                            <img src={`img/items/${kartState.img}`} />
                        </div>
                        <div>
                        <select name="karts" id="karts" value={formKart} onChange={(e) => setFormKart(e.target.value)}>
                            {karts.map(kart => {
                                return <option value={kart.id}>{kart.name}</option>
                            })}
                        </select><br />
                        <button type="button" value="submit" onClick={() => dispatch(updateKart(formKart))}>Update Kart</button>
                        </div>
                    </form>
                    </div>
                    <div className="col d-flex justify-content-center">
                    <form>
                        <div>
                            <img src={`img/items/${tireState.img}`} />
                        </div>
                        <div>
                        <select name="tires" id="tires" value={formTire} onChange={(e) => setFormTire(e.target.value)}>
                            {tires.map(tire => {
                                return <option value={tire.id}>{tire.name}</option>
                            })}
                        </select><br />
                        <button type="button" value="submit" onClick={() => dispatch(updateTire(formTire))}>Update Tire</button>
                        </div>
                    </form>
                    </div>
                    <div className="col d-flex justify-content-center">
                    <form>
                        <div>
                            <img src={`img/items/${gliderState.img}`} />
                        </div>
                        <div>
                        <select name="gliders" id="gliders" value={formGlider} onChange={(e) => setFormGlider(e.target.value)}>
                            {gliders.map(glider => {
                                return <option value={glider.id}>{glider.name}</option>
                            })}
                        </select><br />
                        <button type="button" value="submit" onClick={() => dispatch(updateGlider(formGlider))}>Update Glider</button>
                        </div>
                    </form>
                    </div>
                </div>
            </Fade>
        </div>
        <Chart 
            type="bar"
            data={{
                labels: ["Weight", "Acceleration", "On-Road Traction", "Off-Road Traction", "Mini-Turbo", "Ground Speed", "Water Speed", "Anti-Gravity Speed", "Air Speed", "Ground Handling", "Water Handling", "Anti-Gravity Handling", "Air Handling"],
                datasets: [
                    {
                        label: "Driver",
                        data: [driverState.wg, driverState.ac, driverState.on, driverState.of, driverState.mt, driverState.sl, driverState.sw, driverState.sa, driverState.sg, driverState.tl, driverState.tw, driverState.ta, driverState.tg],
                        backgroundColor: [
                            'rgba(0, 70, 240, 0.8)',
                        ],
                        borderColor: [
                            'rgba(0, 70, 240, 1)',
                        ],
                        borderWidth: 1
                    },
                    {
                        label: "Body",
                        data: [kartState.wg, kartState.ac, kartState.on, kartState.of, kartState.mt, kartState.sl, kartState.sw, kartState.sa, kartState.sg, kartState.tl, kartState.tw, kartState.ta, kartState.tg],
                        backgroundColor: [
                            'rgba(10, 191, 2, 0.8)',
                        ],
                        borderColor: [
                            'rgba(10, 191, 2, 1)',
                        ],
                        borderWidth: 1
                    },
                    {
                        label: "Tire",
                        data: [tireState.wg, tireState.ac, tireState.on, tireState.of, tireState.mt, tireState.sl, tireState.sw, tireState.sa, tireState.sg, tireState.tl, tireState.tw, tireState.ta, tireState.tg],
                        backgroundColor: [
                            'rgba(255, 245, 16, 0.8)',
                        ],
                        borderColor: [
                            'rgba(255, 245, 16, 1)',
                        ],
                        borderWidth: 1
                    },
                    {
                        label: "Glider",
                        data: [gliderState.wg, gliderState.ac, gliderState.on, gliderState.of, gliderState.mt, gliderState.sl, gliderState.sw, gliderState.sa, gliderState.sg, gliderState.tl, gliderState.tw, gliderState.ta, gliderState.tg],
                        backgroundColor: [
                            'rgba(254, 56, 55, 0.8)',
                        ],
                        borderColor: [
                            'rgba(254, 56, 55, 1)',
                        ],
                        borderWidth: 1
                    }
                ]
            }}
            height={100}
            options={{
                // indexAxis: "y",
                maintainAspectRatio: true,
                layout: {
                    padding: 40
                },
                responsive: true,
                plugins: {
                    legend: {
                        position: 'left',
                        reverse: true,
                        labels: {
                            color: 'rgba(255, 255, 255)',
                            boxHeight: 30,
                            // padding: 50
                        }
                    }
                },
                scales: {
                    x: {
                        stacked: true,
                        ticks: {
                            color: 'rgba(255, 255, 255)'
                        }
                    },
                    y: {
                        stacked: true,
                        ticks: {
                            color: 'rgba(255, 255, 255)'
                        }
                    }
                }
            }}
        />
    </>
    )
}

export default Builder

// 'rgba(54, 162, 235, 0.5)',
